DELIMITER //
CREATE PROCEDURE InsertarUsuarios(IN u_Nombre VARCHAR(50),
    IN u_Direccion VARCHAR(60),
    IN u_Telefono CHAR(10),
    IN u_Correo VARCHAR(60),
    IN u_Contrasena VARCHAR(50),
    IN u_Rol INT
)
BEGIN
    INSERT INTO `BDTOYS`.`USUARIOS` (`NOMBRE`,`DIRECCION`,`TELEFONO`,`CORREO`,`CONTRASEÑA`, `ROL`) VALUES (u_Nombre,
        u_Direccion,
        u_Telefono,
        u_Correo,
        u_Contrasena,
        u_Rol
    );
END //
