select * from inv_suc;
SELECT * FROM INVENTARIO;
SELECT * FROM SUCURSALES;
SELECT I.NOMBRE,CONCAT(INS.CANTIDAD,' ',I.PRESENTACION) AS CANTIDAD FROM SUCURSALES S
INNER JOIN INV_SUC INS ON S.ID_SUC = INS.SUCURSAL
INNER JOIN INVENTARIO I ON I.ID_INS = INS.INVENTARIO
WHERE ID_SUC = 1;